
public class SwitchCa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int week = 2;
	        String day;

	        // switch statement to check day
	        switch (week) {
	            case 1:
	                day = "Sunday";
	                break;
	            case 2:
	                day = "Monday";
	                break;
	            case 3:
	                day = "Tuesday";
	                break;
	                default:
	                	day ="No Day";
	                	break;
	        }
	                	System.out.println(day);
	}

}
