
public class NForL {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Nested for loop
		for(int i = 0; i<3;i++) {
			System.out.println("For loop number " + i);
		
		for (int j = 1; j<=3 ; j++) {
			System.out.println("nested for loop number " + i + j);
			
		}
		}
	}

}
